const gitbookTreeview = require('./lib');


var arr = [];

if (process.argv.length === 2) {
	console.log('miss parameters');
	return;
}

for (var i = 2; i < process.argv.length; i++) {
	var num = parseInt(process.argv[i]);
	if (Number.isInteger(num) && (num >= 1 && num <= 6)) {
		arr.push(num);
	} else {
		console.log('error header');
		return;
	}
}

var min = 6;

for (var i = 0; i < arr.length; i++) {
	if (arr[i] <= min) {
		min = arr[i];
	}
}

for (var i = 0; i < arr.length; i++) {
	console.log(gitbookTreeview.getLevelTab(arr, i, min) + '- ' + arr[i]);
}
